import "./Card.css";
import Heart from "../../img/heart.svg";
import HeartFilled from "../../img/heartFilled.svg";
import Comment from "../../img/comment.svg";
import Share from "../../img/share.svg";
import Info from "../../img/info.svg";
import { useState } from "react";

const Card = ({ post, socket, user }) => {
  const [liked, setLiked] = useState(false);

  const handleNotification = (type) => {
    type === 1 && setLiked(true);
    socket.emit("sendNotification", {
      senderName: user,
      receiverName: post.username,
      type,
    });
  };

  return (
    <div className="card">
      <div className="info">
        <img src={post.userImg} alt="dnvus" className="userImg" />
        <span>{post.fullname}</span>
      </div>
      <img src={post.postImg} alt="dnvus" className="postImg" />
      <div className="interaction">
        {liked ? (
          <img src={HeartFilled} alt="hf" className="cardIcon" />
        ) : (
          <img
            src={Heart}
            alt="ht"
            className="cardIcon"
            onClick={()=> handleNotification(1)}
          />
        )}
        <img src={Comment} alt="coment" className="cardIcon" onClick={()=> handleNotification(2)} />
        <img src={Share} alt="shared" className="cardIcon" onClick={()=> handleNotification(3)} />
        <img src={Info} alt="information" className="cardIcon infoIcon"/>
      </div>
    </div>
  );
};

export default Card;
